import React from 'react';
import axios from 'axios';
import '../App.css';

class DetailedArticle extends React.Component {
    state = {
        article:'',
        err:''
      };

    componentDidMount = () => {
        alert("mount detailed article component");
        localStorage.setItem("currentTab","detailed_article");

        if(localStorage.getItem("displayDetailedArticle") === "block"){
            // axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
        axios.get('http://127.0.0.1:5000/article/source='+localStorage.getItem('source') + "&articleid=" + localStorage.getItem("article_id"))
        .then(res => {
          const dataFromServer = res.data;
          console.log(dataFromServer);
          this.setState({ article: dataFromServer });

        })
        .catch(error => {
          this.setState({err:error.message})
        })
        }
    }

    render() {
        return (
            <div className="detailed_article">
               <p>this is detailed article page,article title{this.state.article.title}</p>
            </div>
        )
    }
}

export default DetailedArticle
