    //     text = text.substring(0,text.length-4) + "...";
    //     document.getElementsByClassName("card_description")[0].innerHTML = text;
    // 
    }
    render() {
        return (
                <div className="card" >
                    <div className="row no-gutters">
                        <div className="card_img_div col-md-3">
                            <div className="img_div"><img src={this.props.card.img} className="card-img" alt="..." /></div>
                        </div>
                        <div className="col-md-9">
                            <div className="card-body">
                                <h5 className="card-title">{this.props.card.title}&nbsp;<IoMdShare className="card_share"/></h5>
                                <div><p className="card-text card_description">{this.props.card.description}</p><span class="trunc">...</span></div>
                                <p className="card-text">
                                    <span className="card_date">{this.props.card.date}</span>
                                    <span className={"card_section " + this.props.card.section}>{this.props.card.section}</span>
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
        )
    }
}


export default CardItem
