import React from "react";
import {withRouter} from 'react-router-dom';
import {IoMdTrash} from 'react-icons/io';
import { ToastContainer, toast,cssTransition,Zoom } from 'react-toastify';


class Trash extends React.Component{
//   state = {
//     inputValue: '',
//     results: [], 
//     selectedResult: ''
// }

  Zoom = cssTransition({
    enter: 'zoomIn',
    exit: 'zoomOut',
    // default to 750ms, can be omitted
    duration: 750,
  });

  renderToast  = () => {

        let message = "Removing " + this.props.card.title;
        toast(message);
        localStorage.removeItem("favorites-" + this.props.card.article_id);
  }


  render(){

    let deleteFavorite = (event) => {
      event.stopPropagation();
      event.preventDefault();
      console.log("delete favorite",this.props.card.title);
      localStorage.removeItem("favorite-" + this.props.card.article_id);
      this.props.history.push('/fav');      
    }

      return (
        <div>
            <ToastContainer className="left" autoClose={false} position="top-center" closeOnClick={true} draggablePercent={60} transition={Zoom}/>
        <IoMdTrash className="card_share" onClick={deleteFavorite}/>
        </div>

      )
  }

}

export default withRouter(Trash);
