import React, { Component } from 'react';
import Cards from "./components/Cards";
import axios from 'axios';
import './App.css';

class App extends Component {
  state = {
    data: '',
    cards : [
      {
        id: 1,
        title: 'card title 1',
        completed: false
      },
      {
        id: 2,
        title: 'card title 2',
        completed: true
      },
      {
        id: 3,
        title: 'card title 3',
        completed: false
      }
    ]
  };

  componentDidMount = () => {
    // to the public DNS of your EC2 instance
    axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
    .then(res => {
      const dataFromServer = res.data;
      this.setState({ data: dataFromServer });
    })
    .catch(error => {
      this.setState({gogo:error.message})
    })
  };

  render() {
    return (
      <div className="App">
          <p>Server is saying:</p>
          <p>
            {this.state.data}
          </p>
          <Cards cards={this.state.cards}/>
      </div>
    );
  }
}

export default App;
