import React, { Component } from 'react'
import {Card, Button} from 'react-bootstrap';
import { MdShare } from 'react-icons/md'
import {IoMdShare} from 'react-icons/io'
import styled from 'styled-components';


export class CardItem extends Component {
    Styles = styled.div`
    .cardImage {
        width:200px;
        height:100px;
    }
    .card {
        // width:200px;
        // height:100px;
    }
`;

    // getStyle = () => {
    //     return {
    //         // background: '#f4f4f4',
    //         // padding:'10px',
    //         // borderBottom:'1px #ccc dotted',
    //         // textDecoration: this.props.card.completed ?
    //         //     'line-through': 'none'
    //         width: '90%'
    //     }
    // };

    render() {
        return (
            <this.Styles>
                {console.log(this.props.card.title)}
                <Card className="card">
                    <Card.Img fluid variant="left" className="cardImg" src={this.props.card.img}/>
                    <Card.Body>
                        <Card.Title>
                            {this.props.card.title}
                            <Button variant="primary">{MdShare}</Button>
                        </Card.Title>
                        <Card.Text>
                            {/* {this.props.card.description} */}
                        </Card.Text>
                    </Card.Body>
                </Card>
            </this.Styles>
        )
    }
}


export default CardItem
