                        <div className="col-md-4">
                            <img src={this.props.card.img} className="card-img" alt="..." />
                        </div>
                        <div className="col-md-8">
                            <div className="card-body">
                                <h5 className="card-title">{this.props.card.title}</h5>
                                <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
        <p className="card-text"><small className="text-muted mr-auto col-md-4">{this.props.card.date}</small><span className="card_section col-md-4">{this.props.card.section}</span></p>
                            </div>
                        </div>
                    </div>
                </div>
        )
    }
}


export default CardItem
