import React from 'react';
import axios from 'axios';
import {EmailShareButton,FacebookShareButton,TwitterShareButton,EmailIcon,
    FacebookIcon,TwitterIcon} from 'react-share';
import { FaRegBookmark } from 'react-icons/fa'


import '../App.css';

class DetailedArticle extends React.Component {
    state = {
        article:'',
        err:''
      };

    componentDidMount = () => {
        // alert("mount detailed article component");

        document.getElementsByClassName("loading")[0].style.display = "block";

        localStorage.setItem("currentTab","detailed_article");
        let article_id = this.props.location.search;

        for(let i = 0; i < 3; i++){
            document.getElementsByClassName("navitem_not_shown")[i].style.display = "none";
        }
        // localStorage.setItem("detailed_article_id",this.props.card.article_id);

        console.log("will send request to server in detailed_article tab");
        // axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
        axios.get('http://127.0.0.1:5000/article'+article_id + '&source=' + localStorage.getItem('source'))
        .then(res => {
          const dataFromServer = res.data;
          if(res.status == 200){
            console.log("请求已完成");
            document.getElementsByClassName("loading")[0].style.display = "none";
            document.getElementsByClassName("detailed_article")[0].style.display = "block";
          }
          this.setState({ article: dataFromServer });
        })
        .catch(error => {
          this.setState({err:error.message})
        })

    }

    render() {
        return (
        <div className="detailed_article">

            <div class="card mb-3 detail_card">
                <p class="card-title detail_title">{this.state.article.title}</p>
                <p className="card_date detail_date col-md-3">{this.state.article.date}</p>
                <div classname="detail_share col-md-9">
                    <FacebookShareButton className="detail_share_btn" url={this.state.article.url} hashtag="#CSCI_571_NewsApp">
                        <FacebookIcon size={36} round={true} /> 
                    </FacebookShareButton>
                    <TwitterShareButton className="detail_share_btn" url={this.state.article.url} hashtags={["CSCI_571_NewsApp"]}>
                        <TwitterIcon size={36} round={true} /> 
                    </TwitterShareButton>
                    <EmailShareButton className="detail_share_btn" url={this.state.article.url} subject="#CSCI_571_NewsApp">
                        <EmailIcon size={36} round={true} />
                    </EmailShareButton>

                    <FaRegBookmark className="detail_bookmark" />
                </div>

                <img src={this.state.article.img} class="card-img-top" alt="..." />
        <p class="card-text">{this.state.article.description}</p>
                {/* <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p> */}

            </div>

        </div>
        )
    }
}

export default DetailedArticle
