import React from 'react';
import axios from 'axios';

import '../App.css';

class DetailedArticle extends React.Component {
    state = {
        article:'',
        err:''
      };

    componentDidMount = () => {
        // alert("mount detailed article component");

        document.getElementsByClassName("loading")[0].style.display = "block";

        localStorage.setItem("currentTab","detailed_article");
        let article_id = this.props.location.search;

        for(let i = 0; i < 3; i++){
            document.getElementsByClassName("navitem_not_shown")[i].style.display = "none";
        }
        // localStorage.setItem("detailed_article_id",this.props.card.article_id);

        console.log("will send request to server in detailed_article tab");
        // axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
        axios.get('http://127.0.0.1:5000/article'+article_id + '&source=' + localStorage.getItem('source'))
        .then(res => {
          const dataFromServer = res.data;
          console.log(dataFromServer);
          if(res.status == 200){
            console.log("请求已完成");
            document.getElementsByClassName("loading")[0].style.display = "none";
          }
          this.setState({ article: dataFromServer });
        })
        .catch(error => {
          this.setState({err:error.message})
        })

    }

    render() {
        return (
        <div className="detailed_article">

            <div class="card mb-3">
                <img src={this.state.article.img} class="card-img-top" alt="..." />
                <div class="card-body">
                    <h5 class="card-title">Card title</h5>
                    <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                    <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                </div>
            </div>

        </div>
        )
    }
}

export default DetailedArticle
