import React from 'react';
import CardItem from './CardItem';
import axios from 'axios';
// import SearchCards from './SearchCards';
import {CardDeck,CardColumns,Container} from 'react-bootstrap';
import SearchRow from './SearchRow';

class Favorites extends React.Component{

    state = {
        cards: [],
        err: '',
        row:0
      };

    componentDidMount = () => {

        document.getElementsByClassName("loading")[0].style.display = "block";
        document.getElementsByClassName("search")[0].style.display = "none";
        document.getElementsByClassName('bookmark_sign_yes')[0].style.display = "inline";
        document.getElementsByClassName('bookmark_sign_no')[0].style.display = "none";

        localStorage.setItem("currentTab","favorites");

        for(let i = 0; i < 3; i++){
            document.getElementsByClassName("navitem_not_shown")[i].style.display = "none";
        }

        var storage = JSON.parse(window.localStorage);
        console.log(storage);


    }


    render(){
        return (
            <div className="search">

                <div className="container-fluid">
                    <h3 className="left">Favorites</h3>
                    {
                        this.state.cards.map(
                            (row) => {
                                return <SearchRow key={this.state.row++} row={row} />
                            }
                        )
                    }
                </div>

            </div>
        )
    }
}

export default Favorites;
