import React from 'react';
import {Nav, Navbar,Form,FormControl} from 'react-bootstrap';
import { FiBookmark } from 'react-icons/fi'
import styled from 'styled-components';
import {BrowserRouter, Route, Switch,Link } from 'react-router-dom';
import Home from './Home';
import { World } from './World';
import { Politics } from './Politics';
import { Sports} from './Sports';
import { Technology } from './Technology';
import { Business } from './Business';
import { NoMatch } from './NoMatch';
import {Layout} from './Layout';
import '../App.css';



const Styles = styled.div`
    // .navbar {
    //     width:100%;
    //     height: 8%;
    //     background-color: #222;
    // }

    // .navbar-brand, .navbar-nav .nav-link {
    //     color: #bbb;
    //     &:hover {
    //         color:white;
    //     }
    // }

    // .ml-auto {
    //     position:fixed;
    //     left:0;
    // }
`;

// const handleSwitch = () => {
//     return null;
// }

// a functional component returns a react element
export const NavigationBar = () => (
    // <Styles>
        <BrowserRouter>
            <Navbar className="navbar" collapseOnSelect bg="primary" variant="dark" expand="sm">
                <Form inline>
                        <FormControl type="text" placeholder="Enter keyword" className="input-search" />
                        {/* <Button type="submit">Submit</Button> */}
                </Form>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" className="justify-content-center"/>
                <Navbar.Collapse id='responsive-navbar-nav'>
                    <Nav>
                    {/* className='justify-content-center' */}
                        <ul className="nav navbar-nav">
                        <Nav.Link as={Link} to="/" href="/">Home</Nav.Link>
                        <Nav.Link as={Link} to="/world" href="/world">World</Nav.Link>
                        <Nav.Link as={Link} to="/politics" href="/politics">Politics</Nav.Link>
                        <Nav.Link as={Link} to="/business" href="/business">Business</Nav.Link>
                        <Nav.Link as={Link} to="/technology" href="/technology">Technology</Nav.Link>
                        <Nav.Link as={Link} to="/sports" href="/sports">Sports</Nav.Link>
                        </ul>
                    </Nav>


                    <Nav className='justify-content-end'>
                        <Nav.Item><Nav.Link as={Link} to="#" href="#"> <FiBookmark /> </Nav.Link></Nav.Item>
                        <Navbar.Text>NYTimes</Navbar.Text>
                        <Nav.Item>
                            <div className='custom-control custom-switch'>
                            <input type='checkbox' className='custom-control-input' id='customSwitches' readOnly />
                            <label className='custom-control-label' htmlFor='customSwitches'>
                            Toggle
                            </label>
                        </div>
                        </Nav.Item>
                        <Navbar.Text>Guardian</Navbar.Text>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
            <Switch> 
                <Route exact path="/" component={Home} />
                <Route path="/world" component={World} />
                <Route path="/politics" component={Politics} />
                <Route path="/business" component={Business} />
                <Route path="/technology" component={Technology} />
                <Route path="/sports" component={Sports} />
                <Route component={NoMatch} />
            </Switch>   
          </BrowserRouter>
    // </Styles> 
)
