import React from 'react';
import {Nav, Navbar,Form,FormControl} from 'react-bootstrap';
import Select from 'react-select';
import AsyncSelect from 'react-select/async';
import AutoSelect from './AutoSelect';
import { Search } from "semantic-ui-react";
import { FiBookmark } from 'react-icons/fi'
import styled from 'styled-components';
import {BrowserRouter,Route, Switch, Link } from 'react-router-dom';
import { browserHistory } from 'react-router';
import Home from './Home';
import World from './World';
import Politics from './Politics';
import Sports from './Sports';
import Technology from './Technology';
import Business from './Business';
// import NoMatch from './NoMatch';
import Layout from './Layout';
import {default as SourceSwitch} from "react-switch";
import '../App.css'; 
import DetailedArticle from './DetailedArticle';
import _ from "lodash";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


// a functional component returns a react element
class  NavigationBar extends React.Component {
    constructor(){
        super();

        if (localStorage.getItem('switchChecked') == null){
            localStorage.setItem('switchChecked',"true");
        } // nothing--> true(g) default
        if (localStorage.getItem("source") == null){
            localStorage.setItem("source","g");
        }

        this.state = {
            options : [
                { value: 'chocolate', label: 'Chocolate' },
                { value: 'strawberry', label: 'Strawberry' },
                { value: 'vanilla', label: 'Vanilla' }
              ],
              inputValue: '',
            results: [], selectedResult: ''
        }
    }


    handleSwitch = (checked) => {
        localStorage.setItem('switchChecked', JSON.stringify(checked));
        if (localStorage.getItem('switchChecked') === "true"){
            localStorage.setItem('source',"g");
        } else {
            localStorage.setItem('source',"n");
        }
        window.location.reload();
    }


//     handleInputChange = (newValue) => {
//     const inputValue = newValue.replace(/\W/g, '');
//     this.setState({ inputValue });
//     return inputValue;
//   };


//   filterColors = (inputValue) => {
//       console.log("in filter color");
//       console.log(inputValue);
//     return ['aaa','bbb'];
//   };

  handleSearchChange = async (event) => {
      console.log("in handleSearchChange function");
      var value = this.state.selectedResult;
      console.log("value to query",value);

    try {
      const response = await fetch(
        `https://siqidu.cognitiveservices.azure.com/bing/v7.0/suggestions?q=`+value,
        {
          headers: {
            "Ocp-Apim-Subscription-Key": "1840826bbfa341e68c780e23fdd205c8"
          }
        }
      );
      const data = await response.json();
      console.log(data);
      const resultsRaw = data.suggestionGroups[0].searchSuggestions;
      const results = resultsRaw.map(result => ({ value: result.displayText, label: result.displayText }));
    // const results = resultsRaw.map(result => (result.displayText));
      console.log("suggestion results:",results);
      this.setState({ results });
      return this.state.results;
    } catch (error) {
      console.error(`Error fetching search ${value}`);
    }
  };
  handleResultSelect = (e,newValue) =>{
      console.log("in handleResultSelected"); 
      console.log("newValue",newValue);
    //   console.log("action",e);
    //   console.log(e.target.value);
    // this.setState({ selectedResult: newValue });


    // console.log(document.getElementsByName("aselect")[0]);
  }


    render(){
        return (
            <BrowserRouter>
            <Navbar collapseOnSelect variant="dark" expand="lg">
                <Nav.Item className="nav_select col-lg-3 col-md-5 col-sm-7 col-xl-8">
                    <div>
                             <AsyncSelect className="aselect"
                             cacheOptions
          loadOptions={_.debounce(this.handleSearchChange, 1000, {
            leading: true
          })}

          onInputChange={this.handleResultSelect}

        />
        {/* <AutoSelect result={this.state.selectedResult} /> */}
                            {/* <a style={{ color: "black" }} 
                                href={this.state.selectedResult && this.state.selectedResult.url}>
                                {this.state.selectedResult && this.state.selectedResult.title}
                            </a> */}
                    </div>
                </Nav.Item>
                <Navbar.Toggle aria-controls="responsive-navbar-nav"/>
                <Navbar.Collapse id='responsive-navbar-nav'>
                    <Nav className="mr-auto">
                        <Nav.Item><Nav.Link as={Link} to="/" href="/" className="navs home">Home</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/world" href="/world" className="navs world">World</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/politics" href="/politics" className="navs politics">Politics</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/business" href="/business" className="navs business">Business</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/technology" href="/technology" className="navs technology">Technology</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/sport" href="/sport" className="navs sports" >Sports</Nav.Link></Nav.Item>
                    </Nav>
                    <Nav>
                        <Nav.Item className="navitem_bookmark"><Nav.Link as={Link} to="#" href="#"> <FiBookmark className="bookmark_sign"/> </Nav.Link></Nav.Item>
                        <Nav.Item className="navitem_not_shown"><Navbar.Text className="switch_text1">NYTimes</Navbar.Text></Nav.Item>
                        <Nav.Item className="navitem_not_shown nav_switch"><SourceSwitch className="switch" 
                                    checked={JSON.parse(localStorage.getItem('switchChecked'))}
                                    onChange={this.handleSwitch}
                                    uncheckedIcon={false}
                                    checkedIcon={false} 
                                    onColor="#2196F3" offColor="#C8C8C8"/></Nav.Item>
                        <Nav.Item className="navitem_not_shown"><Navbar.Text className="switch_text2">Guardian</Navbar.Text></Nav.Item>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            <Switch className="page"> 
                <Route exact path="/" render={(props)=> <Home {...props} source={localStorage.getItem('source')} />} />  
                <Route path="/world" render={(props)=> <World {...props} source={localStorage.getItem('source')} />} /> 
                <Route path="/politics" render={(props)=> <Politics {...props} source={localStorage.getItem('source')} />} /> 
                <Route path="/business" render={(props)=> <Business {...props} source={localStorage.getItem('source')} />} /> 
                <Route path="/technology" render={(props)=> <Technology {...props} source={localStorage.getItem('source')} />} /> 
                <Route path="/sport" render={(props)=> <Sports {...props} source={localStorage.getItem('source')} />} /> 
                <Route path='/article' component={DetailedArticle} />
              </Switch>  
        </BrowserRouter>

        )
    }
}

export default NavigationBar
