import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component {
  state = {
    data: ''
  };

  componentDidMount = () => {
    // Make sure to change the (localhost) on the line bellow 
    // to the public DNS of your EC2 instance
    //public DNS http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/
    axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
    .then(res => {
      const dataFromServer = res.data;
      this.setState({ data: dataFromServer });
    });
  };

  render() {
    return (
      <div className="App">
          <p>Server is saying:</p>
          <p>
            {this.state.data}
          </p>
      </div>
    );
  }
}

export default App;
