import React from 'react';
import CardItem from './CardItem';
import axios from 'axios';
import SearchCards from './SearchCards';

class Search extends React.Component{

    state = {
        cards: [],
        err: ''
      };

    componentDidMount = () => {

        // document.getElementsByClassName("loading")[0].style.display = "block";

        // localStorage.setItem("currentTab","search");


        for(let i = 0; i < 3; i++){
            document.getElementsByClassName("navitem_not_shown")[i].style.display = "none";
        }

        console.log("will send request to server in search tab");
        let searchQuery = localStorage.getItem("searchQuery");
        // axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
        axios.get('http://127.0.0.1:5000/search?q='+ searchQuery)
        .then(res => {
          const dataFromServer = res.data;
          if(res.status == 200){
            console.log("search请求已完成");
            // document.getElementsByClassName("loading")[0].style.display = "none";
            // document.getElementsByClassName("detailed_article")[0].style.display = "block";
        }
          this.setState({ cards: dataFromServer });
        })
        .catch(error => {
          this.setState({err:error.message})
        })
    }


    render(){
        return (
            <div className="search">
                <SearchCards cards = {this.state.cards} />
            </div>
        )
    }
}

export default Search;
