import React from "react";
import _ from "lodash";
import AsyncSelect from 'react-select/async';
import {withRouter} from 'react-router-dom';


class Select extends React.Component{
  state = {
    inputValue: '',
    results: [], 
    selectedResult: '',
    valueToSearch:{label:'',value:''}
}

// componentWillReceiveProp() {
//   console.log("in select",window.location.href);

// }

// componentDidMount(){
//   console.log("component mounted");
// }

  handleSearchChange = async (event) => {
    console.log("in handle search change locad the new options");
    var value = this.state.inputValue;
    console.log("value to query",value);

  try {
    const response = await fetch(
      `https://siqidu.cognitiveservices.azure.com/bing/v7.0/suggestions?q=`+value,
      {
        headers: {
          "Ocp-Apim-Subscription-Key": "1840826bbfa341e68c780e23fdd205c8"
        }
      }
    );
    const data = await response.json();
    const resultsRaw = data.suggestionGroups[0].searchSuggestions;
    const results = resultsRaw.map(result => ({ value: result.displayText, label: result.displayText }));
    this.setState({ results: results });
    return this.state.results;
  } catch (error) {
    console.error(`Error fetching search ${value}`);
  }
};

handleResultSelect = (newValue) =>{
  console.log("newVlue",newValue);
  this.setState({ inputValue: newValue });
}

getSearchResult = (option) =>{
    let value = option['value'];
    this.setState({ selectedResult : value });
    localStorage.setItem("searchQuery",value);
    console.log(this.state); 

    this.props.history.push('/search/'+value);     
}


  render(){
      return (
        <AsyncSelect className="select" placeholder="Enter Keyword..." cacheOptions 
        loadOptions={_.debounce(this.handleSearchChange, 1000, {leading: true})} 
        onInputChange={this.handleResultSelect} 
        onChange={this.getSearchResult} 
        value = {this.state.inputValue}/>
      )
  }
}

export default withRouter(Select);
