import React from 'react';
import axios from 'axios';

import '../App.css';

class Home extends React.Component {
    state = {
        data: []
        // cards : [
        //   {
        //     id: 1,
        //     title: 'card title 1',
        //     completed: false
        //   },
        //   {
        //     id: 2,
        //     title: 'card title 2',
        //     completed: true
        //   },
        //   {
        //     id: 3,
        //     title: 'card title 3',
        //     completed: false
        //   }
        // ]
      };

    componentDidMount = () => {
        // to the public DNS of your EC2 instance
        axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
        .then(res => {
          const dataFromServer = res.data;
          const cards = [];
          for (var i = 0 ; i< dataFromServer.length; i++){
            var card = dataFromServer[i];
            console.log(card);
            cards.push(card);
          }
          this.setState({ data: cards });
        })
        .catch(error => {
          this.setState({gogo:error.message})
        })
      };

    render() {
        return (
            <div className="tab">
                <p>{this.state.data}</p>
            </div>
        )
    }
}

export default Home
