import React from 'react';
import {Nav, Navbar,Form,FormControl} from 'react-bootstrap';
import { FiBookmark } from 'react-icons/fi'
import styled from 'styled-components';
import {BrowserRouter,Route, Link } from 'react-router-dom';
import Home from './Home';
import World from './World';
import Politics from './Politics';
import Sports from './Sports';
import Technology from './Technology';
import Business from './Business';
import NoMatch from './NoMatch';
import Layout from './Layout';
import Switch from "react-switch";
import '../App.css'; 


// a functional component returns a react element
class  NavigationBar extends React.Component {
    constructor(){
        super();
        // localStorage.setItem('switchChecked',"false");
    }
    state = {
        // switchChecked: true
    }

    handleSwitch = (checked) => {
        console.log("@@@@@@@@@@@in handle switch" + checked);
        localStorage.setItem('switchChecked', JSON.stringify(checked));
        // this.setState({switchChecked: checked});
        window.location.reload();
        console.log("1234567897654321" + localStorage.getItem('switchChecked'));
        // console.log(typeof(JSON.parse(localStorage.getItem('switchChecked'))));
    }

    render(){
        return (
            <BrowserRouter>
            <Navbar collapseOnSelect bg="primary" variant="dark" expand="sm">
                {/* select */}
                <Navbar.Toggle aria-controls="responsive-navbar-nav"/>
                <Navbar.Collapse id='responsive-navbar-nav'>
                    <Nav className="mr-auto">
                        <Nav.Item><Nav.Link as={Link} to="/" href="/">Home</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/world" href="/world">World</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/politics" href="/politics">Politics</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/business" href="/business">Business</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/technology" href="/technology">Technology</Nav.Link></Nav.Item>
                        <Nav.Item><Nav.Link as={Link} to="/sport" href="/sport">Sports</Nav.Link></Nav.Item>
                    </Nav>
                    <Nav>
                        <Nav.Item><Nav.Link as={Link} to="#" href="#"> <FiBookmark/> </Nav.Link></Nav.Item>
                        <Nav.Item>
                            <Navbar.Text>NYTimes</Navbar.Text>
                            <div className="switch_label">
                                <Switch className="switch" 
                                    checked={JSON.parse(localStorage.getItem('switchChecked'))}
                                    onChange={this.handleSwitch}
                                    uncheckedIcon={false}
                                    checkedIcon={false} 
                                    height={24} width={48} 
                                    onColor="#2693e6 " offColor="#f4f4f4"/>
                            </div>
                            <Navbar.Text>Guardian</Navbar.Text>
                        </Nav.Item>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            <div className="page"> 
                  <Route exact path="/" component={Home} />
                  <Route path="/world" component={World} />
                  <Route path="/politics" component={Politics} />
                  <Route path="/business" component={Business} />
                  <Route path="/technology" component={Technology} />
                  <Route path="/sport" component={Sports} />
                  {/* <Route component={NoMatch} /> */}
              </div>  
        </BrowserRouter>

        )
    }
}

export default NavigationBar
