import React from "react";
import _ from "lodash";
import AsyncSelect from 'react-select/async';
import {BrowserRouter,Route, Switch, Link,Redirect,withRouter} from 'react-router-dom';


class Select extends React.Component{
  state = {
    inputValue: '',
    results: [], 
    selectedResult: ''
}

  handleSearchChange = async (event) => {
    var value = this.state.inputValue;
    console.log("value to query",value);

  try {
    const response = await fetch(
      `https://siqidu.cognitiveservices.azure.com/bing/v7.0/suggestions?q=`+value,
      {
        headers: {
          "Ocp-Apim-Subscription-Key": "1840826bbfa341e68c780e23fdd205c8"
        }
      }
    );
    const data = await response.json();
    const resultsRaw = data.suggestionGroups[0].searchSuggestions;
    const results = resultsRaw.map(result => ({ value: result.displayText, label: result.displayText }));
    this.setState({ results: results });
    return this.state.results;
  } catch (error) {
    console.error(`Error fetching search ${value}`);
  }
};

handleResultSelect = (newValue) =>{
  this.setState({ inputValue: newValue });
}

getSearchResult = (option) =>{
    let value = option['value'];
    // console.log("value:",value);
    this.setState({ selectedResult : value });
    this.props.history.push('/search?q='+value);      
}


  render(){
      return (
        <AsyncSelect cacheOptions loadOptions={_.debounce(this.handleSearchChange, 1000, {leading: true})} onInputChange={this.handleResultSelect} 
        onChange={this.getSearchResult} value = {this.state.inputValue}/>
      )
  }
}

export default withRouter(Select);
