import React from 'react';
import '../App.css';

class Home extends React.Component {
    componentDidMount = () => {
        // to the public DNS of your EC2 instance
        axios.get(`http://ec2-54-146-190-253.compute-1.amazonaws.com:5000/`)
        .then(res => {
          const dataFromServer = res.data;
          this.setState({ data: dataFromServer });
        })
        .catch(error => {
          this.setState({gogo:error.message})
        })
      };

    render() {
        return (
            <div className="tab">
                <p>to display long cards</p>
            </div>
        )
    }
}

export default Home
